#设置MySQL客户端连接服务器所用字符集为UTF8
SET NAMES UTF8;
#丢弃数据库(如果存在的话)langq
DROP DATABASE IF EXISTS langq;
#创建数据库langq，使用字符集为UTF8
CREATE DATABASE langq CHARSET=UTF8;
#进入数据库langq
USE langq;
/*创建“商城系列”相关的表*/
CREATE TABLE lq_watch(
  	wid 		INT PRIMARY KEY AUTO_INCREMENT,#编号
	title 		VARCHAR(64) NOT NULL DEFAULT"",#商品名
	wname 		VARCHAR(32) NOT NULL DEFAULT"",#系列名
	price 		decimal(10,2) NOT NULL DEFAULT 0.00,#价格	
	wglass  	VARCHAR(16) NOT NULL DEFAULT"",#表镜
	size    	VARCHAR(32) NOT NULL DEFAULT"",#尺寸
	proof   	VARCHAR(32) NOT NULL DEFAULT"",#防水性能
	mater		VARCHAR(32) NOT NULL DEFAULT"",#材质
	shape		VARCHAR(32) NOT NULL DEFAULT"圆形",#形状
	needle		VARCHAR(32) NOT NULL DEFAULT"",#指针
	color		VARCHAR(32) NOT NULL DEFAULT"",#颜色
	runtime		VARCHAR(32) NOT NULL DEFAULT"",#运行时间
	engine		VARCHAR(32) NOT NULL DEFAULT"",#机芯
	tengine		VARCHAR(32) NOT NULL DEFAULT"",#计时码表机芯
	etype		VARCHAR(32) NOT NULL DEFAULT"",#机芯类型
	func		VARCHAR(32) NOT NULL DEFAULT"",#功能
	cmater		VARCHAR(32) NOT NULL DEFAULT"",#表链材质
	clasp		VARCHAR(32) NOT NULL DEFAULT"",#表扣
	diamond		VARCHAR(32) NOT NULL DEFAULT"无",#钻石
	shell		VARCHAR(32) NOT NULL DEFAULT"玻璃",#表壳
	band		VARCHAR(32) NOT NULL DEFAULT"皮革",#表带
	carat		VARCHAR(32) NOT NULL DEFAULT"0",#克拉
	deep		VARCHAR(32) NOT NULL DEFAULT"0",#防水深度
	detailsp1	VARCHAR(128) NOT NULL DEFAULT"",#介绍1
	detailsp2	VARCHAR(128) NOT NULL DEFAULT"",#介绍2
	seq_new_arrival TINYINT NOT NULL DEFAULT 0,#新品
	seq_top_sale TINYINT NOT NULL DEFAULT 5 #热销		
);
CREATE TABLE lq_watch_pic(
  	wname        	VARCHAR(32) NOT NULL DEFAULT"",#系列名
  	title			VARCHAR(64) NOT NULL DEFAULT"",#商品名
	sm				VARCHAR(128) NOT NULL DEFAULT"",#小图
	md 				VARCHAR(128) NOT NULL DEFAULT"",#中图 
	lg				VARCHAR(128) NOT NULL DEFAULT"",#大图
	detailsp1		VARCHAR(128) NOT NULL DEFAULT"",#详情1
	detailsp2		VARCHAR(128) NOT NULL DEFAULT"",#详情2
	detailsp3 		VARCHAR(128) NOT NULL DEFAULT""#详情3
);
/*创建“用户系列”相关的表*/
CREATE TABLE lq_user(
  	uid 			INT PRIMARY KEY AUTO_INCREMENT,#用户id
  	uname			VARCHAR(32)	NOT NULL DEFAULT"",#用户姓名
  	gender			TINYINT NOT NULL DEFAULT 1,#性别
  	phone       	VARCHAR(16) NOT NULL DEFAULT"",#手机号码
	email			VARCHAR(64) NOT NULL DEFAULT"",#电子邮件
	upwd 			VARCHAR(32) NOT NULL DEFAULT"",#密码
	uaddress		VARCHAR(128) NOT NULL DEFAULT""#用户地址
);
/*创建“大事记系列”相关的表*/
CREATE TABLE lq_history(
  	hid 			INT PRIMARY KEY AUTO_INCREMENT,#编号
  	hyear       	VARCHAR(32) NOT NULL DEFAULT"",#年
	hmonth			VARCHAR(32) NOT NULL DEFAULT"",#月
	hpic			VARCHAR(128) NOT NULL DEFAULT"",#事记图
	hdesc			VARCHAR(128) NOT NULL DEFAULT"",#事记介绍
	htitle			VARCHAR(64) NOT NULL DEFAULT"",#标题
	stime			VARCHAR(32) NOT NULL DEFAULT""#时间
);
/*创建“大使系列”相关的表*/
CREATE TABLE lq_amba(
  	 aid 			INT PRIMARY KEY AUTO_INCREMENT,#编号
  	 fid			INT NOT NULL DEFAULT 1,#系列
  	desc1       	VARCHAR(128) NOT NULL DEFAULT"",#介绍
	pic1			VARCHAR(128) NOT NULL DEFAULT"",#图片
	desc2			VARCHAR(128) NOT NULL DEFAULT"",#介绍
	pic_lg			VARCHAR(128) NOT NULL DEFAULT""#图片
);
/*创建“影片系列”相关的表*/
CREATE TABLE lq_movie(
  	mid			INT PRIMARY KEY AUTO_INCREMENT,#编号
  	mdesc		VARCHAR(128) NOT NULL DEFAULT"",#影片描述
  	mpic       	VARCHAR(128) NOT NULL DEFAULT"",#手机号码
	mhref		VARCHAR(128) NOT NULL DEFAULT"" #电子邮件
);


